query drawQuery(
  $first: Int!
  $lastId: String
  $prizeVaultAddress: String!
  $beforeTimestamp: Int!
) {
  accounts(first: $first, where: { id_gt: $lastId, prizeVault_: { id: $prizeVaultAddress } }) {
    id
    user {
      address
    }

    balanceUpdatesBeforeTimestamp: balanceUpdates(
      orderBy: timestamp
      orderDirection: desc
      first: 1
      where: { timestamp_lte: $beforeTimestamp }
    ) {
      delegateBalance
    }
  }
}
